import React, { useRef, useEffect } from 'react';

import './Map.css';

const Map = props => {


  const { center, zoom } = props;


    const map = new window.google.maps.Map(document.getElementById('maping'), {
      center: center,
      zoom: zoom
    });
  // Marker
    new window.google.maps.Marker({ position: center, map: map });
  // Marker
   /*dependency: when center or zoom changes, the above function run again. */

  return (
    <div
      id="maping"
      className={`map ${props.className}`}
      style={props.style}
    ></div>
  );
};

export default Map;
